var modalCart=document.querySelector(".modal-cart"),modalCartClose=modalCart.querySelector(".modal-close"),modalCartReturn=modalCart.querySelector(".cart-button"),catalogList=document.querySelector(".catalog-list");catalogList.addEventListener("click",a=>{a.preventDefault(),a.target.classList.contains("buy-button")&&modalCart.classList.add("modal-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&modalCart.classList.contains("modal-show")&&modalCart.classList.remove("modal-show")}),modalCartClose.addEventListener("click",a=>{a.preventDefault(),modalCart.classList.remove("modal-show")}),modalCartReturn.addEventListener("click",a=>{a.preventDefault(),modalCart.classList.remove("modal-show")});
